namespace Pokupo\BlogBundle\Entity\Repository;

use DateTime;
use Pokupo\BlogBundle\Entity\Post;
use Pokupo\BlogBundle\Entity\PostClick;
use Doctrine\ORM\EntityRepository;

/**
 * PostClickRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PostClickRepository extends EntityRepository
{
    /**
     * @param Post $post
     */
    public function add(Post $post)
    {
        $date = new DateTime();

        $click = $this->findOneBy(
            [
                'post_id' => $post->getId(),
                'date' => $date,
            ]
        );
        if (!$click) {
            $click = new PostClick();
            $click->setPost($post);
            $click->setDate($date);
        }

        $click->increment();

        $em = $this->getEntityManager();
        $em->persist($click);
        $em->flush();
    }
}
